import React, { Component } from 'react';
import ImgBox from "./boxes/imgBox.portfolio";
import data from '../json_files/data.json'


class Portfolio extends Component {

    constructor(){
        super();
        this.state = {
            imges : []
        }
    }

    componentDidMount(){
        this.setState({
            imges : data.portfolio
        })
        console.log(data.portfolio);

    }


    mapping(matrix){
        matrix.map( (res, id) => {
            return(
                <ImgBox
                img = {res.imagerequire("@fortawesome/free-solid-svg-icons")[data.works[id].icon]}
                id = {id}
                />
                // console.log(typeof res.image)
            )
        })
    }


    render(){
        return(
            <section className='portfolioSection'>
                <div className="container">
                    <div className="specialHeading">
                        <h1>My Portfolio</h1>
                    </div>
                    <div className="otherContent">
                        <ul>
                            <li>All</li>
                            <li>HTML</li>
                            <li>SASS</li>
                            <li>ReactJS</li>
                        </ul>
                        <div className="boxesContainer">
                            {this.mapping(this.state.imges)}
                        </div>
                    </div>
                </div>
            </section>
        )
    }

}


export default Portfolio;
