import React, { Component } from 'react'
import CourseList from "./component/CourseList";
import CourseForm from "./component/CourseForm";

class App extends Component{

    constructor(){
        super();
        this.state = {
            info : [
                { id : 1,  courseName : "HTML"},
                // { id : 2,  courseName : "CSS"},
                // { id : 3,  courseName : "JS"},
                // { id : 4,  courseName : "ReactJS"},
            ],

            currentValue : "",
        }

        this.update = this.update.bind(this)
        this.adding = this.adding.bind(this)
        this.removing = this.removing.bind(this)
    }

    mapping(matrix){
        return(matrix.map(
            (res, id)=>{          
                console.log(id);  
                return(
                <CourseList 
                courseName = {res.courseName}
                key        = {id}
                removing   = {this.removing}

                />
            )}
        )
    )}

    update(e){

        this.setState({
            currentValue : e.target.value
        })
    }


    adding(e){
        e.preventDefault();
        const {info} = this.state;
        const {currentValue} = this.state;
        if (currentValue.trim().length !==0) {
            e.id = Math.random()

            info.push({ id : e, courseName : currentValue})
            this.setState({
                currentValue : "",
                info
            })            
            console.log("The value is: " + this.state.currentValue );

        }else{
            alert("No Baby!")
        }


    }


    removing(id){
        const {info} = this.state;
        const index = info.findIndex( item => item.id === id)
        console.log(index);
        info.splice(index,1)
        this.setState({
            info
        })
    }



    render(){
        return(
            <div className='container'>
                    {
        console.log(this.state.info)

                    }
                    <CourseForm currentValue = {this.state.currentValue} adding = {this.adding}  update = {this.update}/>
                <ul>
                    {this.mapping(this.state.info)}
                </ul>
            </div>
        )
    }
}

export default App
