import React, { Component } from 'react'
import CourseList from "./component/CourseList";
import CourseForm from "./component/CourseForm";

class App extends Component{

    constructor(){
        super();
        this.state = {
            info : [
                { courseName : "HTML"},
                { courseName : "CSS"},
                {  courseName : "JS"},
                // {  courseName : "ReactJS"},
            ],

            currentValue : "",
        }

        this.update = this.update.bind(this)
        this.adding = this.adding.bind(this)
        this.removing = this.removing.bind(this)
    }

    mapping(matrix){
        const checking = matrix.length ? 
        matrix.map(
            (res, id)=> {
                return(
                <CourseList 
                courseName = {res.courseName}
                key        = {id}
                removing   = {()=> this.removing(id)}

                />
                )}
        )
        :
            <h1 className='warnning'>There Is No Info To Show ...</h1>;
        return checking
    }

    update(e){

        this.setState({
            currentValue : e.target.value
        })
    }

    adding(e){
        const {info} = this.state;
        info.push(this.state.currentValue);


    }



    render(){
        return(
            <div className='container'>
                    <CourseForm currentValue = {this.state.currentValue} adding = {this.adding}  update = {this.update}/>
                <ul>
                    {this.mapping(this.state.info)}
                </ul>
            </div>
        )
    }
}

export default App












