import React, { Component }  from 'react'


class CourseForm extends Component{
    constructor(){
        super();
        this.state ={
            isCorrect : false,
        }
        this.isEdit= this.isEdit.bind(this);
    }

    isEdit(){
        let {isCorrect} = this.state;
        this.setState({
            isCorrect : !isCorrect,
        })
        console.log(isCorrect);
        return isCorrect;
    }

    originalForm() {
        return(
        <form onSubmit={this.props.adding} action="" className='myForm'>
            <input value={this.props.currentValue} onChange={this.props.update} type="text" className='courseInput' placeholder='Enter A Course...' />
            <button type='submit'>Edit  </button>
            <button type='submit'>Submit</button>
        </form>
        )
    }

    newForm(){
        return(
            <form onSubmit={this.isEdit} >
                <input type="text" />
                <button>Upload</button>
            </form>
        )
    }

    render(){
        return(
            <div>
                {this.originalForm()}
            </div>
        )
    }
}

export default CourseForm
