import React, { Component } from 'react'
import CourseList from "./component/CourseList";
import CourseForm from "./component/CourseForm";

class App extends Component{

    constructor(){
        super()
        this.state={
            courses : [
                {  courseName : "HTML"       },
                {  courseName : "CSS"        },
                {  courseName : "JavaScipt"  },
                {  courseName : "Python"     },
                {  courseName : "PHP"        },
            ],
            current : '',
        }
        this.update = this.update.bind(this)
        this.addingCourse = this.update.addingCourse(this)
    }

    // Update The Changed Values
    update(e){
        console.log(e.target.value);
        this.setState({
            current : e.target.value
        })
    }

    addingCourse(e){
        e.preventDefault();
        console.log("The Value is: "+ this.state.current);
    }


    mapping(matrix){
        const m = matrix.map( (course,id)=> 
            <CourseList 
            courseName  =  {course.courseName} 
            key         =  {id}
            />
        )
        return m
    }



    render(){
        return(
            <div className='container'>
                <CourseForm
                update ={this.update}
                addingCourse ={this.addingCourse}

                />
                <ul>
                    {this.mapping(this.state.courses)}
                </ul>
            </div>
        )
    }
}

export default App
