import { faTimes } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon }     from '@fortawesome/react-fontawesome';
import React, { Component }  from 'react'


class CourseList extends Component{

        constructor(){
        super();
        this.state ={
            isCorrect : false,
        }
        this.isEdit= this.isEdit.bind(this);
        this.newForm= this.newForm.bind(this);
    }

    isEdit(){
        let {isCorrect} = this.state
        this.setState({
            isCorrect : !isCorrect
        })
    }

    apply(e){
        e.preventDefault()
        this.props.uploadNewValue(this.props.index, this.input.value)


    }

    newForm(){
        return(
            <form onSubmit={this.apply}>
                <input type="text" ref={(v)=>this.in} defaultValue={this.props.courseName}/>
                <button>Upload</button>
            </form>
        )
    }
    originalForm(){
        return(
            <li >
                {this.props.courseName}
                <button type="submit" onClick={()=>this.isEdit()}>Edit</button>
                <FontAwesomeIcon 
                    onClick   =  {() => this.props.removing(this.props.key)} 
                    icon      =  { faTimes  } 
                    className =  'closeIcon'>
                </FontAwesomeIcon>
            </li>
        )
    }

    render(){
        return(
            <div>
                { this.state.isCorrect ?  this.newForm()  : this.originalForm()}
            </div>
        )
    }

}

export default CourseList
