import React, { Component } from 'react'
import CourseList from "./component/CourseList";
import CourseForm from "./component/CourseForm";

class App extends Component{

    constructor(){
        super();
        this.state = {
            info : [
                {courseName : "HTML"},
                {courseName : "CSS"},
                {courseName : "JS"},
                {courseName : "ReactJS"},
            ],

            currentValue : "",
        }

        this.update = this.update.bind(this)
        this.adding = this.adding.bind(this)
    }

    mapping(matrix){
        return(matrix.map(
            (res, id)=>{            

                return(
                <CourseList 

                courseName = {res.courseName}
                key        = {id}
                />

            )}
        )
    )}

    update(e){
        this.setState({
            currentValue : e.target.value
        })
    }


    adding(e){
        e.preventDefault();
        const {info} = this.state;
        const {currentValue} = this.state;
        if (currentValue.value.length !==0) {
            info.push({ courseName : currentValue})
            this.setState({
                info
            })            
            console.log("The value is: " + this.state.currentValue );

        }else{
            alert("No Baby!")
        }


    }
    render(){
        return(
            <div className='container'>
                    <CourseForm adding = {this.adding}  update = {this.update}/>
                <ul>
                    {this.mapping(this.state.info)}
                </ul>
            </div>
        )
    }
}

export default App
