import React, { Component }  from 'react';
import Box from "./boxes/box.work";
import { faBolt, faLink, faTachometerAlt } from '@fortawesome/free-solid-svg-icons';
import data from '../js/data.json'
class Work extends Component{

            state = {
                work : [],
            }

    componentDidMount(){

        this.setState({
            work : data.works
        })

    }

    mapping(matrix){

        return( matrix.map( (res, id) => 
                <Box icon= {require("@fortawesome/free-solid-svg-icons")[data.works[id].icon]} 
                    title = {res.title}
                    body = {res.body}
                />
        )
       )

    }


    render(){


        return(
            <section className='workSection'>
                <div className="container">
                    <div className="specialHeading">
                        <h1>My Works</h1>
                    </div>
                    <div className="boxesContainer">
                        {this.mapping(this.state.work)}

                        {/* <Box icon= {faLink} title = {"Mobile UX"}/> */}
                        {/* <Box icon= {faBolt} title = {"Web App"}/> */}
                        {/* <Box icon= {faTachometerAlt} title = {"Designs"}/> */}
                    </div>
                </div>
            </section>
    )
    }

}


export default Work;
