import { faTimes } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon }     from '@fortawesome/react-fontawesome';
import React, { Component, Fragment }  from 'react'


class CourseList extends Component{

        constructor(){
        super();
        this.state ={
            isCorrect : false,
        }
        this.isEdit= this.isEdit.bind(this);
        this.newForm= this.newForm.bind(this);
    }

    isEdit(){
        const {isCorrect} = this.state;
        this.setState({
            isCorrect : !isCorrect
        })
    }

    apply=(e)=>{

        e.preventDefault();
        this.props.uploadNewValue(this.props.index, this.input.value)
        this.isEdit()
    }


    newForm(){
        return(
            <form onSubmit={   this.apply  }>
                <input ref={   (v) =>{ this.value = v  } } type="text" defaultValue={this.props.courseName}/>
                <button>Upload</button>
            </form>
        )
    }
    originalForm(){
        return(
            <li >
                {this.props.courseName}
                <div className="leftSide">
                    <button type="submit" onClick={()=>this.isEdit()}>Edit</button>
                    <span className="closeBtns">
                        <FontAwesomeIcon 
                            onClick   =  {() => this.props.removing(this.props.index)} 
                            icon      =  { faTimes  } 
                            className =  'close'>
                        </FontAwesomeIcon>
                    </span>
                </div>
            </li>
        )
    }

    render(){
        return(
            <div>
                { this.state.isCorrect ?  this.newForm()  : this.originalForm()}
            </div>
        )
    }

}

export default CourseList
