import React, { Component } from 'react'
import CourseList from "./component/CourseList";
import CourseForm from "./component/CourseForm";

class App extends Component{

    constructor(){
        super()
        this.state={
            courses : [
                {  courseName : "HTML"       },
                {  courseName : "CSS"        },
                {  courseName : "JavaScipt"  },
                {  courseName : "Python"     },
                {  courseName : "PHP"        },
            ],
            current : '',
        }
        this.update = this.update.bind(this)
        this.addingCourse = this.addingCourse.bind(this)
    }

    // Update The Changed Values
    update(e){
        this.setState({
            current : e.target.value
        })
    }

    addingCourse(e){
        e.preventDefault();

        if(this.state.current.trim().length === 0 ){
            alert(":(Fill The Fields.")
        }{
            const {current} = this.state
            const {courses} = this.state
            courses.push({courseName: current})
            this.setState({
                courses,
                current: ''
            })
            console.log("The Value is: "+ this.state.current);

        }
    }


    mapping(matrix){
        const m = matrix.map( (course,id)=> 
            <CourseList 
            courseName  =  {course.courseName} 
            key         =  {id}

            />
        )
        return m
    }



    render(){
        return(
            <div className='container'>
                <CourseForm
                update ={this.update}
                addingCourse ={this.addingCourse}
                current      ={this.state.current}

                />
                <ul>
                    {this.mapping(this.state.courses)}
                </ul>
            </div>
        )
    }
}

export default App
