import React, { Component } from 'react'
import CourseList from "./component/CourseList";
import CourseForm from "./component/CourseForm";

class App extends Component{

    constructor(){
        super();
        this.state = {
            info : [
                {id : '' ,  courseName : "HTML"},
                {id : '' , courseName : "CSS"},
                {id : '' , courseName : "JS"},
                {id : '' , courseName : "ReactJS"},
            ],

            currentValue : "",
        }

        this.update = this.update.bind(this)
        this.adding = this.adding.bind(this)
        this.removing = this.removing.bind(this)
    }

    mapping(matrix){
        return(matrix.map(
            (res)=>{            

                return(
                <CourseList 

                courseName = {res.courseName}
                id        = {res.id}
                removing   = {this.removing}
                />

            )}
        )
    )}

    update(e){
        this.setState({
            currentValue : e.target.value
        })
    }


    adding(e){
        e.preventDefault();
        let myItem = e.id = Math.random()
        const {info} = this.state;
        const {currentValue} = this.state;
        if (currentValue.trim().length !==0) {
            e.id = Math.random()
            info.push({ 
                courseName : currentValue ,
                })
            this.setState({
                id : myItem,
                currentValue : "",
            })            
            console.log("The value is: " + this.state.currentValue );

        }else{
            alert("No Baby!")
        }
    }


    removing(id){
        const {info} = this.state;
        let index = info.findIndex( item => item.id === id)
        info.splice(index,1)
        this.setState({
            info
        })
    }




    render(){
        return(
            <div className='container'>
                    <CourseForm currentValue = {this.state.currentValue} adding = {this.adding}  update = {this.update}/>
                <ul>
                    {this.mapping(this.state.info)}
                </ul>
            </div>
        )
    }
}

export default App
