import React, { Component } from 'react'
import CourseList from "./component/CourseList";
import CourseForm from "./component/CourseForm";

class App extends Component{

    constructor(){
        super()
        this.state={
            courses: [
                {  courseName : "HTML"       },
                {  courseName : "CSS"        },
                {  courseName : "JavaScipt"  },
                {  courseName : "Python"     },
                {  courseName : "PHP"        },
            ]
        }

    }

    mapping = ()=>{
        this.state.courses.map( (course, id)=> {
            return(
                    <CourseList courseName={course.courseName} key={id} />
            )
        })

    }



    render(){
        return(
            <div className='container'>
            </div>
        )
    }
}

export default App
